import { FlatList, Text, View, StyleSheet, Image} from "react-native";
import RepositoryItem from "./RepositoryItem";
import useRepositories from "../hooks/useRepositories";
import usePokemon from "../hooks/usePokemon";

const styles = StyleSheet.create({
  container: {
    padding: 20,
    paddingBottom: 5,
    paddingTop: 5,
  },
  strong: {
    color: "#09f",
    fontWeight: "bold",
    marginBottom: 5,
  },
  language: {
    padding: 7,
    color: theme.colors.white,
    backgroundColor: theme.colors.primary,
    alignSelf: "flex-start",
    borderRadius: 4,
    marginVertical:4,
    overflow: "hidden",
  },
  image: {
    width: 48,
    height: 48,
    borderRadius: 4,
  },
});

const RepositoryList = () => {
  const repositories  = useRepositories();



  if(repositories){
    return (

      repositories.map((poke)=> {
      
       console.log("pokemon",poke)

        
        return (
          <View>
            <Text>{poke.name}</Text>
            <Image style={styles.image} source={{ uri: "https://as01.epimg.net/meristation/imagenes/2022/07/21/noticias/1658389050_817312_1658389673_noticia_normal.jpg" }} />
            
       
            
           
          </View>
        )
      })
   
    );
  }


};

export default RepositoryList;
