import { FlatList, Text, View, StyleSheet, Image, ScrollView} from "react-native";
import RepositoryItem from "./RepositoryItem";
import useRepositories from "../hooks/useRepositories";
import usePokemon from "../hooks/usePokemon";
import theme from "../theme.js";

const styles = StyleSheet.create({
  pokemonName:{
    fontSize:20,
  },
  image: {
    width: 200,
    height:200,
  },
  container:{
    borderColor:"#000",
    
  }
});

const RepositoryList = () => {
  const repositories  = useRepositories();



  if(repositories){
    return (
<ScrollView>
{repositories.map((poke,index)=> {
      
     

       
       return (
         <View style={styles.container}  key={index}>
          <Image style={styles.image} source={{ uri:poke.sprites.front_default}} />
           <Text style={styles.pokemonName}  >{poke.name}</Text>
           
           
      
           
          
         </View>
       )
     })}
  </ScrollView>
    
   
    );
  }


};

export default RepositoryList;
