import { FlatList, Text, View, StyleSheet, Image, ScrollView} from "react-native";
import RepositoryItem from "./RepositoryItem";
import useRepositories from "../hooks/useRepositories";
import usePokemon from "../hooks/usePokemon";
import theme from "../theme.js";
import useAllPokemons from "../hooks/useAllPokemons";
import {Link, useLocation} from "react-router-native"

const styles = StyleSheet.create({
  pokemonName:{
    fontSize:20,
  },
  image: {
    width: 200,
    height:200,
  },
  containerPlant:{
    borderColor:"#49D0B0",
    borderWidth:1,
    backgroundColor:"#49D0B0",
    marginBottom:2,
    flexDirection:"row",
    justifyContent:"center",
    alignItems:"center"
  },
  containerFire:{
    borderColor:"#F96C6C",
    borderWidth:1,
    backgroundColor:"#F96C6C",
    marginBottom:2,
    flexDirection:"row",
    justifyContent:"center",
    alignItems:"center"
  }
});

const RepositoryList = () => {
  const repositories  = useRepositories();
  


  if(repositories){
    return (
<ScrollView>
{repositories.map((poke,index)=> {
      
      let typeStyle = ""
      if(poke.types[0].type.name=="fire"){
        typeStyle=styles.containerFire}else{
          typeStyle=styles.containerPlant}
       return (
        <Link to={`/pokemon/${index+1}`} key={index}>
         <View style={typeStyle}  >
          <Image style={styles.image} source={{ uri:poke.sprites.front_default}} />
           <Text style={styles.pokemonName}  >{poke.name}</Text>
           
           
      
           
          
         </View>
         </Link>
       )
     })}
  </ScrollView>
    
   
    );
  }


};

export default RepositoryList;
